        ORG #C000;    WildDOSFileCore#32        LD HL,CORE32,DE,#4000        LD BC,END-START        LDIR        RET        INCL "KERNEL";---------------------------------------RMFL2   LD A,L:AND #F0:LD L,A        LD C,32:ADD HL,BC        JP NXFL2;-------NXTETY2 ;i:DE - Address;A -   [7]:1 - only short names;    [6-5]:reserved;      [4]:1 - enable created time field;      [3]:1 - enable created date field;      [2]:1 - enable size field;    [1-0]:%00 find next Entry;          %01 find next File;          %10 find next DIR;          %11 reserved;;        o: Z - End Of Dir;          NZ - Buffer updated:;          DE - [size(4)],;               [date(2)],;               [time(2)],;               flag(1),Entry Name,#00        LD C,A        XOR A:BIT 7,C:JR Z,$+3:INC A        LD (BT7+1),A        XOR A:BIT 4,C:JR Z,$+3:INC A        LD (BT4+1),A        XOR A:BIT 3,C:JR Z,$+3:INC A        LD (BT3+1),A        XOR A:BIT 2,C:JR Z,$+3:INC A        LD (BT2+1),A        LD A,C:AND %00000011        LD (BT0+1),A        PUSH DE        LD A,(CGFL):OR A:CALL Z,NXTINI        POP DE        LD HL,NXFF2,(NxMR+1),HLNXFF2   LD HL,(CGPZ),B,0NXFL2   LD A,H:CP NXTBE[:JR NC,NXMR        LD A,(HL)        OR A:RET Z        CP #E5:JR Z,RMFL2        LD C,11:ADD HL,BC        LD A,(HL):BIT 3,A:JR NZ,RMFL2        EXABT0     LD A,0        OR A:JR Z,BT1        DEC A:JR NZ,BT01        EXA:BIT 4,A:JR NZ,RMFL2,Bt1BT01    EXA:BIT 4,A:JR Z,RMFL2Bt1     EXABT1     LD C,16-11:ADD HL,BCBT2     LD A,0:OR A:JR Z,BT3        LD C,28-16:ADD HL,BC.4      LDI        LD C,16:SBC HL,BCBT3     LD A,0:OR A:JR Z,BT4.2      LDI.2      DEC LBT4     LD A,0:OR A:JR Z,BT8.2      DEC L.2      LDIBT8     RES 4,L:CALL LNCRC        EXA:LD (DE),A:INC DEBT7     LD A,0:OR A:JP Z,NXTE2        LD A,L:AND #F0:LD L,A        JP Snm;---------------------------------------NXMR    LD A,(EOC):CP #0F:RET Z        PUSH DE        LD HL,NXTBE-#0300        LD DE,NXTBM-#0300        LD BC,#0300:LDIR        LD HL,DE,(CGPZ),HL        LD B,NXTBZ/2:CALL LOAD512        LD (HL),0        POP DENxMR    JP 0RMFL    LD C,32-11:ADD HL,BC:JP NXFLRMFL1   LD C,32:ADD HL,BC:JP NXFL;-------NXTETY  ;i:DE - Address;        o:DE - New Value;           Z - End Of Dir;          NZ - OK;;fclus(4),size(4),date(2),time(2),;!flag(1),name(X),#00        PUSH DE        LD A,(CGFL):OR A:CALL Z,NXTINI        POP DE        LD HL,NXFF,(NxMR+1),HLNXFF    LD HL,(CGPZ),B,0NXFL    LD A,H:CP NXTBE[:JR NC,NXMR        LD A,(HL)        OR A:RET Z        CP #E5:JR Z,RMFL1        LD C,11:ADD HL,BC        LD A,(HL):BIT 3,A:JR NZ,RMFL        EXA        LD C,15:ADD HL,BC.2      LDI        LD C,8:SBC HL,BC.2      LDI        ADD HL,BC.4      LDI        LD C,16:SBC HL,BC.2      LDI.2      DEC L.2      DEC L.2      LDI        RES 4,L:CALL LNCRC        EXA        AND #10        XOR #10:LD (DE),A:INC DENXTE2   OR A:LD C,32:SBC HL,BC:JP C,SNM        LD A,(HL):CP #0F:JP NZ,SNM        EXA.2      INC L        CP (HL):JP NZ,SNM2        LD C,1        LD (CGHL),HL,(CGDE),DE        LD A,L:AND #F0:LD L,ALNPARS  LD A,(HL)        BIT 6,A:JR Z,$+4:SET 6,C        CP C:JR NZ,SNM3:INC C        INC L.5      CALL UCH:JR NC,SNMX.3      INC L.6      CALL UCH:JR NC,SNMX.2      INC L.2      CALL UCH:JR NC,SNMX        DEC L        LD A,C:CP #40:JR NC,NXLEXI        OR A:LD C,64-1:SBC HL,BC        LD C,A:JR C,SNM3        JP LNPARSNXLEXI  XOR A:LD (DE),A:INC DE        LD HL,(CGHL)        LD C,32+16+3:ADD HL,BC        LD (CGPZ),HL        LD A,1:OR A        RETSNMX    JR NZ,NXLEXISNM3    LD HL,(CGHL),DE,(CGDE)SNM2    LD A,L:AND #F0:LD L,A        LD C,32:JR SNM+2SNM     LD C,21:ADD HL,BCSnm     LD B,8SNA     LD A,(HL)        CALL Acs:LD (DE),A:INC DE,L        DJNZ SNA        LD B,8SNM_    DEC DE:LD A,(DE)        CP " ":JR NZ,$+4:DJNZ SNM_        INC DE        LD A,L:AND #F0:OR 8:LD L,A        PUSH DE        LD A,".",(DE),A:INC DE_LD A,(HL):CALL Acs:LD (DE),A:INC DE,L_LD A,(HL):CALL Acs:LD (DE),A:INC DE,L_LD A,(HL):CALL Acs:LD (DE),A:INC DE,L        POP BC        BIT 4,(HL):JR Z,SNMD.3      DEC DE:LD A,(DE):CP 32:JR NZ,SNm        LD DE,BC        JR SNMDSNm     INC DESNMD    XOR A:LD (DE),A:INC DE        LD BC,21:ADD HL,BC        LD (CGPZ),HL        LD A,1:OR A        RET;-------UCH     INC L        LD A,(HL):OR A:JR NZ,UCHX        DEC L:LD A,(HL):INC L,L        OR A:JR Z,UCHE        LD (DE),A:INC DE        CP #80:RET CUCH_    XOR A:RETUCh_    INC L:XOR A:RETUCHE    LD A,1:OR A:RETUCHX    CP 4:JR NZ,UCh_        DEC L:LD A,(HL):INC L,L        CP #01:JR Z,HYO        CP #10:JR C,UCH_        CP #3F+1:JR C,DGG        CP #4F+1:JR C,DEE        CP #51:JR Z,LYO        XOR A        RETDGG     ADD A,#70:LD (DE),A:INC DE        SCF:RETDEE     ADD A,#A0:LD (DE),A:INC DE        SCF:RETHYO     LD A,#F0,(DE),A:INC DE:SCF:RETLYO     LD A,#F1,(DE),A:INC DE:SCF:RET;-------Acs     CP #80:JR NC,RU3        CP #40+1:RET C        CP #5B:RET NC        SET 5,A        RETRU3     CP #F0:JR NZ,$+3:INC A        CP #F1:RET Z        CP #A0:RET NC        CP #90:JR NC,RU4        SET 5,A:RETRU4     ADD A,#50:RET;-------LNCRC   XOR A.11     RRCA:ADD A,(HL):INC L        RET;-------NXTINI  LD HL,1        LD (CGFL),HL        LD HL,LSTCAT:CALL GIPAG        LD HL,NXTBU,(CGPZ),HL,B,NXTBZ        CALL LOAD512        LD (HL),0        RET;---------------------------------------GENTRY  ;i:HL(32) -> ENTRY        LD DE,ENTRY,BC,32:LDIR        RETTENTRY  ;i:DE(32) <- ENTRY        LD HL,ENTRY,BC,32:LDIRZ0      RET;GIVE/TAKE Active DIR:GLSTCAT ;i:HL(4) - CLUS num        LD DE,LSTCAT,BC,4:LDIR        LD (CGFL),BC        RETTLSTCAT ;i:DE(4) - Addres of value        LD HL,LSTCAT,BC,4:LDIR        RET;---------------------------------------;READ SECTORS WITH POSITIONING:RDD     ;i:[DE,HL] - SEC number;               BC - Address;                A - number        PUSH BC        CALL XPOZI        POP HL        JP RDDSE;SAVE SECTORS WITH POSITIONING:SDD     ;i:[DE,HL] - SEC number;               BC - Address;                A - number        PUSH BC        CALL XPOZI        POP HL        JP SDDSE;---------------------------------------;SKIP DATA from FAT32:LOADNON LD DE,Z0,(NW0+1),DE        CALL LOAD512        LD DE,RDDSE,(NW0+1),DE        RET;READ DATA from FAT32(256+256):LOAD256 LD DE,RDD256,(NW0+1),DE        CALL LOAD512        LD DE,RDDSE,(NW0+1),DE        RET;SAVE DATA to FAT32 (STREAM):SAVE512 LD DE,SDDSE,(NW0+1),DE        CALL LOAD512        LD DE,RDDSE,(NW0+1),DE        RET;---------------------------------------;READ DATA from FAT32 (STREAM):LOAD512 ;i:HL - Address;           B - lenght (512b blocks);     CUHL(4) - CLUSnum (if EOC+NSDC=0!);        o:HL - New value;           A - EndOfChain        XOR A:LD (ABT),A        CALL LPREX:JR NZ,RH        LD A,B:CALL NEWCLARH      LD HL,(LDHL)        LD A,(EOC)        RET;Positioning to Cluster, if needed:LPREX   LD (LDHL),HL        LD A,(EOC):OR A:RET NZ        LD A,(NSDC):OR A:JR NZ,RX        PUSH BC:LD HL,CUHL:CALL GIPAG        POP BC        RETRX      XOR A        RET;-------NEWCLA  LD (BZN),ANXTC    LD HL,(LTHL),DE,(LTDE):CALL PROZ        LD HL,BZN        LD A,(BSECPC),BC,(NSDC)        SUB C:LD B,A        LD A,(HL):OR A:RET Z        SUB B:JR NC,KN        ADD A,B:LD B,A:XOR AKN      LD (HL),A        LD A,B,(NR0),A        LD HL,(LDHL)NW0     CALL RDDSE;       READ Sector(s)        LD (LDHL),HL;   updating Address        LD HL,LTHL,DE,LLHL,BC,4:LDIR        LD HL,(LTHL),DE,(LTDE)_LD BC,(NR0):ADD HL,BC:JR NC,$+3:INC DE        LD (LTHL),HL,(LTDE),DE        LD HL,NSDC,A,C:ADD A,(HL)        LD (HL),A        LD BC,(BSECPC)        CP C:JP C,NXTC;  End OF Cluster?;                        YES!        LD HL,(CUHL),DE,(CUDE)        CALL CURIT,GIPAG:JP Z,NXTC        RET;------Searching for free Cluster:------SRHFCL  LD HL,FSTFRC,DE,CAHL,BC,4:LDIRSRHFC   ;i:[CADE,CAHL]-Cluster number;        o:[DE,HL]- Cluster number;          EXX HL - position in BUFFER+4        LD HL,(CAHL),DE,(CADE)        CALL CURIT:RET CFC      LD A,(HL):INC HL        OR (HL):INC HL        OR (HL):INC HL        OR (HL):INC HL:JR Z,GETZE        EX DE,HL        LD HL,CAHL        INC (HL):JP NZ,AGA:INC HL        INC (HL):JR NZ,AGA:INC HL        INC (HL):JR NZ,AGA:INC HL        INC (HL)AGA     EX DE,HL        LD A,H:CP SECBE[:JP C,FC        LD HL,(LTHL),DE,(LTDE):INC HL        LD A,H:OR L:JR NZ,$+3:INC DE        CALL XPOZI        LD HL,SECBU,A,1:CALL RDDSE        LD HL,SECBU        JP FC;-------GETZE   EXX        LD HL,(CAHL),DE,(CADE)        PUSH HL:LD HL,CAHL:CALL INC4b        POP HL        XOR A        RET;------Read Sector from FAT:------------CURIT   ;i:[DE,HL]-Cluster number;        o:SECBU(512);          HL-poz in SECBU where Cluster        CALL DEL128.2      SLA C:RL B        PUSH BC        LD (LSTSE+2),DE,(LSTSE),HL        LD BC,(BFTSZ+2)        LD A,D:CP B:JR C,JK        LD A,E:CP C:JR C,JK        LD BC,(BFTSZ)        LD A,H:CP B:JR C,JK        LD A,L:CP C:JR NC,FATENDJK      LD BC,(SFAT)        CALL ADD4B        CALL XSPOZ        CALL XPOZI        LD HL,SECBU,A,1:CALL RDDSE        POP BC        LD HL,SECBU:ADD HL,BC        XOR A        RET;-------FATEND  POP BC        SCF        RET;----Pos. to First Sector of Cluster:---GIPAG   ;i:HL(4) - Cluster number        CALL TOS        LD E,(HL):INC HL        LD D,(HL):INC HL        LD A,(HL):INC HL        LD H,(HL),L,A:OR H,E,D:JR Z,RDIR        LD A,H:AND #0F:CP #0F:JR Z,MDC        EX DE,HLPOM     LD (CUHL),HL,(CUDE),DE_LD BC,2:OR A:SBC HL,BC:JR NC,$+3:DEC DE        LD A,(BSECPC):CALL UMNOX2        LD BC,(SDFAT):CALL ADD4B        EX DE,HL        LD BC,(SDFAT+2):ADD HL,BC        EX DE,HL        CALL XSPOZ  