;Wild Player plugin;(C) KOSHI/MGN 2018;---------------------------------------        ORG #6000;-------WLD     EQU #6006;-------        DS 16        DB "WildCommanderMDL";    Header        DB #0A;                  Version        DB 0;                       Type        DB 3; Pages        DB 0; Page to #8000        DB 0,16; CODE        DB 1,32; PLAYERS1        DB 2,7;  PLAYERS2.3      DB 0,0        DS 16        DB "C  "        DB "M  "        DB "MUZ"        DB "$M "        DB "$P "        DB "ASC"        DB "SQT"        DB "STC"        DB "FLS"        DB "STP"        DB "FTC"        DB "PSC"        DB "PT1"        DB "PT2"        DB "PT3"        DB "GTR"        DB "TFC"        DB "VTX"        DB "VT "        DB "FXM"        DB "SAA"        DB "P_M"        DB "P_C"        DB "P_S"        DB "P_A"        DB "P_P"        DB "P_F"        DB "P_G"        DB "P_V"        DB "P_T"        DB "AYL"        DB "M3U"        DB 0        DW #0000,#000F;                        Max File Size        DB "Wild Player Plug-in v1.7        "; Plugin Name        DB #04;                                Type        DS 6        DB "Play by Wild Player     ";---------------------------------------        ORG #8000,#6200;-------;Main WindowMDXzero EQU 19;       X without PlayListMDX     EQU 4;        XMDY     EQU 5;        YMDW     EQU 42;       widthMDH     EQU 15;       height;Analyzer WindowANX     EQU MDXANY     EQU MDY+MDHANW     EQU MDWANH     EQU 4;PlayList WindowPLX     EQU MDX+MDWPLY     EQU MDYPLW     EQU 30PLH     EQU MDH+ANH;Help WindowHLPX    EQU 9HLPY    EQU 0HLPW    EQU 62HLPH    EQU 6HLPC    EQU %01001111CURclr  EQU %01011000CURmsk  EQU %00000111MDC     EQU %01111111;-------INSTM   EQU #C000PLAYM   EQU #C005STOPM   EQU #C008;-------PT3PL   EQU #0BE1PT2PL   EQU #0A0CFMPL    EQU #0743VTPL    EQU #086E;-------SECBU   EQU #9E00LOBU    EQU #A000;---------------------------------------WP333   LD (DAIX),IX        LD (DADE),HL        LD (FILEINF+1),BC        XOR A        LD (WINDZ+1),A        INC A        LD (ESTAT),A        LD A,MDX        LD (MNWND+2),A        LD (ANWND+2),A        CALL INITZ        CALL EXTCHE:JR Z,NOPL;AYL/M3U:        XOR A        LD (ESTAT),A        CALL EXGENPL        CALL GEDPLPLSTART CALL NEED        JP ENTR;STOP;-------YEMR    ;Marked Files -> Play List        CALL MGENPL        LD HL,(FILEINF+1),(HL),0        JR PLSTARTNOPL    LD HL,0:CALL TMRKDFL:JR NZ,YEMRNOMR    ;CALL UGENPL        LD A,1        LD (PLMD),A        LD A,MDXzero        LD (MNWND+2),A        LD (ANWND+2),ANEW     CALL NP6,NP06        LD B,16+32,HL,LOBU:CALL LOAD512        LD B,32:CALL LOAD512        LD A,(DETKA):OR A:CALL NZ,TFMDET        CALL ZAPUMZ,GEDPL        CALL NEED        LD A,(MUZPL):OR A:JR NZ,MAIN        LD A,(Mloop):OR A:JP Z,NXTMZMAIN    EI:HALT:CALL ATIT        CALL PMACH:JP NZ,NXTMZ        CALL CLOCK,ANALYZE,ANPR        CALL LMD,Z,PLSHOW        LD IX,PLWND        CALL LMD:JR NZ,WOPL        CALL CURSOR        CALL UPPP,NZ,CRUP        CALL DWWW,NZ,CRDNWOPL    CALL F1KE,NZ,HLPSHOW        CALL F2KE,NZ,MODLOOP        CALL F3KE,NZ,MODSTEP        CALL ENKE:JP NZ,ENTR        CALL SPKE:JR NZ,NXTMZ        CALL ESC:JR Z,MAIN        XOR A:LD (ESTAT),AEXI3    EI:HALT        CALL DEFNT        LD IX,HLPWND:CALL HMD,NZ,RRESB        LD IX,PLWND:CALL LMD,Z,RRESB        LD IX,ANWND:CALL RRESB        CALL AYOFF        LD IX,MNWND:CALL RRESBEXI2    LD A,(ESTAT):CP 2:RET C        CALL LMD:LD A,2:RET NZ        XOR A        RETLMD     LD A,(PLMD):OR A:RETHMD     LD A,(HPMD):OR A:RET;-------NXTMZ   LD A,(Mloop)        CP 2:JR Z,ENTR; ONE        CP 1:JR Z,STOP; STOP        CALL LMD:JR NZ,ENTR        LD IX,PLWND        LD L,(IX+22),H,(IX+23)        LD C,(IX+12)        PUSH HL,BC:CALL CRDN        POP BC,HL        LD A,(IX+22):CP L:JR NZ,ENTR        LD A,(IX+23):CP H:JR NZ,ENTR        LD A,(IX+12):CP C:JR NZ,ENTR        CALL CURSER        CALL PLINT        LD A,(MUZPL):OR A:JP Z,MAINENTR    CALL AYOFF        CALL LMD:JP NZ,EXI3        CALL GWZ:JP C,EXI3        LD DE,SECBU        PUSH DE:XOR A:LD (DE),A:INC DE        LD BC,#FF0E:CALL Ldr        XOR A:LD (DE),A        POP HL        CALL FENTRY:JP Z,EXI3;LD A,D:OR E:JR NZ,EXI3        LD (DADE),HL        CALL GFILE        JP NEWSTOP    LD HL,#0100,(MUZPL),HL        CALL AYOFF        JP MAIN;-------CRUP    LD IX,PLWND        LD A,(IX+12):CP 1:JR Z,SRLUP        CALL CURSER:DEC (IX+12)        CALL CURSOR        RETSRLUP   LD L,(IX+22),H,(IX+23)        LD A,L:OR H:RET Z        DEC HLGLOB    LD (IX+22),L,(IX+23),H        LD (IX+24),1        RETCRDN    LD IX,PLWND        LD A,(IX+12)        CP (IX+13):JR NC,SRLDN        CALL CURSER:INC (IX+12)        CALL CURSOR        RETSRLDN   INC (IX+12):CALL GWZ        DEC (IX+12):RET C        LD L,(IX+22),H,(IX+23):INC HL        JR GLOBMODLOOP LD HL,Mloop,DE,MLOOP:JP TTTMODSTEP LD HL,Mstep,DE,MSTEP:JP TTT;---------------------------------------INITZ   XOR A        LD (PLMD),A        LD (HPMD),A        LD A,(Mloop):INC A:RET NZ        CALL MODLOOP        CALL MODSTEP        RET;---------------------------------------HLPSHOW LD IX,HLPWND        CALL HMD:JR Z,HLPshow        XOR A:LD (HPMD),A:JP RRESBHLPshow LD A,1,(HPMD),A:JP PRWOW;---------------------------------------PLSHOW  LD IX,PLWND,A,(IX+24):OR A:RET Z        DEC (IX+24)        LD HL,#0101,(PLHL),HL        XOR A:LD (LDR+1),A        LD L,(IX+22),H,(IX+23)        LD B,(IX+5):DEC B,B:LD (IX+13),BLSH     PUSH HL,BC:CALL PRLIN        POP BC,HL:INC HL:DJNZ LSH        RETPRLIN   CALL GWE        CALL LDR:JR NC,LuR        LD A,32,(DE),A:INC E:DJNZ $-2LuR     LD BC,PLW-2        LD HL,(PLHL),DE,HL        INC H:LD (PLHL),HL        LD HL,SECBU:CALL PRSRW        RETLDR     LD A,0        LD B,PLW-2,DE,SECBU        OR A:SCF:RET NZ        LD DE,LOBU        LD BC,#FF0E:CALL Ldr:EX DE,HL        XOR A:LD (HL),A,(LOBU-1),A        LD BC,#5C+(128)*256        CALL MJ20:INC HL        LD DE,SECBU,BC,14+(PLW-2)*256MYJ     LD A,(HL):CP C:RET C        LD (DE),A:INC HL,E:DJNZ MYJ        XOR A        RETLdr     LD A,(HL):CP C:RET C        LD (DE),A:INC HL,DE        LD A,H:OR A:JR Z,LdR        LD A,(HL):CP C:RET C        LD (DE),A:INC HL,DE        LD A,H:OR A:JR Z,LdRLD8     DJNZ Ldr        XOR A        RETLdR     LD A,(PLPG):INC A        LD (PLPG),A:CALL NPX        LD HL,#C000        JR LD8GWE     LD A,(LDR+1):OR A:RET NZ        PUSH HL:CALL NPG        POP HL        LD DE,#C000.2      ADD HL,HL        ADD HL,DE        LD C,(HL):INC L        LD E,(HL):INC L        LD D,(HL)        EX DE,HL        LD A,H:CP #C0:JR NC,GWe        LD A,1,(LDR+1),A        LD A,(PLHL+1):DEC A        LD (IX+13),A        RETGWe     LD A,C,(PLPG),A        JP NPXGWZ     LD IX,PLWND        CALL NPG        LD L,(IX+22),H,(IX+23)        LD D,0,E,(IX+12):DEC E        ADD HL,DE        LD DE,#C000.2      ADD HL,HL        ADD HL,DE        LD C,(HL):INC L        LD E,(HL):INC L        LD D,(HL):EX DE,HL        LD A,H:CP #C0:RET C        LD A,C,(PLPG),A        CALL NPX        XOR A        RETPLINT   XOR A        LD (IX+22),A        LD (IX+23),A        INC A        LD (IX+12),A        LD (IX+24),A        RET;---------------------------------------;UGENPL CALL PLINI;LD HL,SECBU,DE,LOBU;PUSH DE;LD BC,8:LDIR;LD A,".",(DE),A:INC DE;LD C,3:LDIR;XOR A:LD (DE),A;POP HL:CALL GENLN;JP PLEND;---------------------------------------MGENPL  CALL PLINI        LD HL,0,(X+1),HL:CALL X        JP PLENDX       LD HL,0        INC HL:LD A,H:CP 8:RET NC        LD (X+1),HL:SET 7,H        LD DE,SECBU:CALL TMRKDFL:RET NZ        LD HL,SECBU:CALL GENLN        JR X;-------PLINI   XOR A        LD (PLPG),A        LD HL,#C000        LD (PLGE),HL        LD (PLHL),HL        RETPLEND   CALL NPG        XOR A        LD HL,(PLGE)        LD (HL),A:INC HL        LD (HL),A:INC HL        LD (HL),A:INC HL        RET;-------GENLN   PUSH HL        CALL NPG        LD DE,(PLGE)        LD HL,PLPG,BC,4:LDIR        LD (PLGE),DE        POP HL:CALL SNDLN        RETSNDL    LD A,13,(DE),A:INC DE        LD A,D        OR A:LD A,(PLPG):JR NZ,NDL        LD DE,#C000:INC ANDL     LD (PLPG),A        LD (PLHL),DE        RETSNDLN   LD A,(PLPG):CALL NPX        LD DE,(PLHL)DLN     LD A,(HL):CP 14:JR C,SNDL        LD (DE),A:INC HL,DE        LD A,D:OR A:JR NZ,DLN        LD A,(PLPG):INC A        LD (PLPG),A:CALL NPX        LD DE,#C000        JR DLN;-------PLPG    NOP ;- !PLHL    DS 3;/PLGE    DS 2;---------------------------------------EXGENPL CALL PLINI        LD HL,LOBU,B,8:CALL LOAD512        LD HL,LOBU,DE,AYLH        CALL SRHMZ:LD DE,LOBU:JR NZ,M3        LD B,0:CALL MJ35        LD A,(HL):CP 10:JR NZ,$+3:INC HL        EX DE,HLM3      EX DE,HL:CALL CABOOM        JP PLENDCABOOM  XOR A:LD (LOBU-1),A        LD DE,(PLGE)OEE     LD A,H:CP LOBU[+#08:JR NC,TLOee     LD A,(HL)        CP "<":JR Z,TWKL        CP "#":JR Z,TWKLMOEe     CALL MJ30:JR NZ,NEE        LD A,(HL)        CP #0A:JR NZ,$+3:INC HL        LD A,(HL):OR A:JR NZ,OEENEE     RET;-------MJ20    DEC HL:LD A,(HL)        OR A:RET Z        CP C:RET Z        CP #0A:RET Z        CP #0D:RET Z        DJNZ MJ20        OR A        RETMJ30    CALL GENLN:INC HL:XOR A:RETMJ35    LD A,(HL):INC HL:CP 14:RET C        DJNZ MJ35        RETTL      LD BC,#0800:OR A:SBC HL,BC        PUSH HL        LD HL,LOBU+#0800        LD DE,LOBU:LDIR        EX DE,HL:LD B,4:CALL LOAD512        POP HL        JR OeeTWKLM   INC HL        LD A,(HL):INC HL        LD B,(HL):DEC HL,HL        CP "E":JR NZ,OEe        LD A,B:CP "X":JR NZ,OEe        JP TWKTWKL    LD B,0:CALL MJ35        LD A,(HL)        OR A:JP Z,NEE        CP #0A:JR NZ,$+3:INC HL        LD A,(HL)        CP ">":JR NZ,TWKLTWK     LD B,0:CALL MJ35        LD A,(HL)        CP #0A:JR NZ,$+3:INC HL        LD A,(HL):OR A:JP Z,NEE        JP Oee;---------------------------------------TTT     LD A,(HL)        PUSH HL:INC HL        LD C,(HL):INC HL        LD B,(HL):INC HL        INC A:CP B:JR C,$+3:XOR A        LD B,0        PUSH AF        OR A:JR Z,UUU        ADD HL,BC:DEC A:JR NZ,$-2UUU     LDIR        POP AF        POP HL        LD (HL),A        RET;---------------------------------------NEED    LD HL,0        LD (P_SH),HL,(TME),HL,(P_NU),HL        CALL WINDZ,MNINF,ANFNT        CALL PLRINF,PRINFO        CALL PBCLC,PBFCLR        RET;---------------------------------------WINDZ   LD A,0:OR A:RET NZ        INC A:LD (WINDZ+1),A        LD IX,MNWND:CALL PRWOWFILEINF LD HL,0        LD DE,MDW-2:ADD HL,DE:LD (HL),0        OR A:SBC HL,DE        LD DE,#0701:CALL TXTPR        LD IX,ANWND:CALL PRWOW        LD IX,PLWND:CALL LMD,Z,PRWOW,PLINT        RETMNINF   LD IX,MNWND        LD HL,TXT1,DE,#0C01:CALL TXTPR        LD HL,TXT2,DE,#0D01:CALL TXTPR        LD HL,MZNM,DE,#0C09,BC,32:CALL PRSRW        LD HL,MZN2,DE,#0D09,BC,32:CALL PRSRW        RETPRINFO  LD IX,MNWND        LD HL,PLT0,DE,#0901:CALL TXTPR        LD HL,PLT1,DE,#0A01:CALL TXTPR        LD A,#DC        LD (SPR+8),A        LD (SPR+18),A        LD A,#DF        LD (SPR+#40+8),A        LD (SPR+#40+18),A        RETPLRINF  CALL NP1        LD HL,PL0,B,26        XOR A:LD (HL),A:INC HL:DJNZ $-2        LD HL,"00",(PL2+1),HL        LD DE,(#C000)        LD HL,PTI,B,15AJ      LD A,(HL):INC HL:CP E:JR NZ,AL        LD A,(HL):CP D:JR Z,A1JAL      INC HL,HL,HL        DJNZ AJ        LD HL,PTIX-1A1J     INC HL        LD (NOWT),BC        LD E,(HL):INC HL        LD D,(HL)        EX DE,HL        LD DE,PL0,C,255        LDI:LD A,(HL):OR A:JR NZ,$-4        LD A,(SAAMZ):OR A:JR NZ,SAA        LD HL,"MF"        LD A,(FMMZ):OR A:JR NZ,A2J        LD HL,"YA"A2J     LD A,(TSMZ)        OR A:JR Z,$+3:LD HL,"ST"        LD (PL1),HL        XOR AA3J     LD (PL1+2),A        RETSAA     LD HL,"AS",(PL1),HL,A,H        JR A3J;---------------------------------------ZAPUMZ  LD HL,#0100,(MUZPL),HL        CALL AYOFF        LD HL,GTPR        LD B,64,A,32,(HL),A:INC HL:DJNZ $-2TRY0    XOR A:LD (FMMZ),A,(SAAMZ),A        LD HL,LOBU,DE,HRFO        CALL SRHMZ:JR NZ,TRY1        LD A,(HL):AND %01111111        CP "1":JR NZ,TRY1        LD HL,(LOBU+4),A,H        CP #50:JP NC,NEmUZ        LD (DADE),HL        LD HL,LOBU,DE,HL:CALL DEHR21TRY1    LD HL,LOBU,DE,PTFO;          PT3        LD A,1,(STA),A        CALL ZATA:JP NZ,TRY1A        XOR A:LD (VTMD),A        LD DE,PTFO        JP TSPT3OTN     LD HL,0        DEC HL        LD DE,LOBU,BC,#4000        LDIR        LD A,(VTMD):OR A:JR Z,NVT        LD DE,VTDL        CALL GETMZ        LD HL,#C000+24,BC,VTLN        CALL GETPL        LD HL,VTDL+30,(ONEB),HL        LD HL,VTDL+66,(TWOB),HL        JR JAZZNVT     LD A,(LOBU+98):CP #20:JR Z,NOTS        LD A,2,(ZZMZ),A,(TSMZ),ANOTS    LD DE,MMODUL        CALL GETMZ        LD HL,#C000+6,BC,PT3LN        CALL GETPL        LD HL,MMODUL+30,(ONEB),HL        LD HL,MMODUL+66,(TWOB),HLJAZZ    LD A,32,(#C00A),A        LD A,5,(CTOL+1),AMUZZ    CALL INSTM;LD A,1,(P_ST),A        LD HL,0,(TMM),HLTRECH   LD A,(ZZMZ):OR A:JR NZ,GG        LD HL,(TSFG),H,0        LD (TSMZ),HLGG      XOR A:LD (ZZMZ),A        LD HL,1,(MUZPL),HL        CALL KOSHI        CALL KPAH        LD A,255,(ESTAT),A        RETKPAH    LD HL,(ONEB),DE,MZNM,BC,32:LDIR        LD HL,(TWOB),DE,MZNM+32,C,32        LDIR        RETNEmUZ   XOR A:LD (PAUS),A        RET;-------KOSHI   ;LD A,1:LD (FILI),A;XOR A:LD (ANCI),A        RETZATA    LD (ZA+1),DE        PUSH HL        LD BC,(DADE)        LD HL,LOBU:ADD HL,BC        LD BC,HL        POP HLZA      LD DE,PTFOZAT     LD A,H:CP B:JR NC,NTY        LD A,(D