ker v1.00",0PLIA    DB "Global Tracker",0PLIB    DB "TFM Compiler v1.x",0PLIC    DB "FLASH Tracker",0PLID    DB "Fuxoft AY Language",0PLIE    DB "Pro Tracker 1",0PLIF    DB "E-Tracker",0PLIX    DB 32,0;-------DIGITS  DB "@DBDFDB";0        DB "@DB20DB"        DB "@DB20DB"        DB "@DFDFDF"        DB "@DCDB20";1        DB "@20DB20"        DB "@20DB20"        DB "@DFDFDF"        DB "@DFDFDB";2        DB "@DCDCDB"        DB "@DB2020"        DB "@DFDFDF"        DB "@DFDFDB";3        DB "@20DCDB"        DB "@2020DB"        DB "@DFDFDF"        DB "@DB20DB";4        DB "@DBDCDB"        DB "@2020DB"        DB "@2020DF"        DB "@DBDFDF";5        DB "@DBDCDC"        DB "@2020DB"        DB "@DFDFDF"        DB "@DBDFDF";6        DB "@DBDCDC"        DB "@DB20DB"        DB "@DFDFDF"        DB "@DFDFDB";7        DB "@2020DB"        DB "@2020DB"        DB "@2020DF"        DB "@DBDFDB";8        DB "@DBDCDB"        DB "@DB20DB"        DB "@DFDFDF"        DB "@DBDFDB";9        DB "@DBDCDB"        DB "@2020DB"        DB "@DFDFDF";-------PTFO    DB "ProTracker 3.",0VORT    DB "Vortex Tracker II 1.",0TFMF    DB "TFMcom1.",0ETFO    DB "ETracker (C) BY ESI",0STPFO   DB "KSA SOFTWARE COMPILATION OF"        DB 0PSCFO   DB "PSC V1.0",0FTFO    DB "Module: ",0        DB ";Fast Tracker v1.",0ASCFO   DB "ASM COMPILATION OF ",0GTFO    DB "GTR",0FXFO    DB "FXSM",0AYLH    DB "ZX Spectrum Sound Chip "        DB "Emulator Play List File "        DB "v1.",0;---------------------------------------MODES   DB 5,5        DB #0B,3,#FE:DW MLOOP        DB 13,13        DB 5,5        DB #FE:DW MSTEP        DB 0MLOOP   DB "      ",0Mloop   DB #FF,6,3        DB " [ALL]"        DB "[STOP]"        DB " [ONE]"MSTEP   DB "         ",0Mstep   DB #FF,9,3        DB "[FORWARD]"        DB "[BCKWARD]"        DB "[SHUFFLE]";-------PLT0    DB 2," Editor:"        DB 7,7PL0     DB "                          "        DB 0PLT1    DB 2